const GameBoard = () => {

    const width = 500;
    const height = 500;
    const numCol = 10;
    const numRow = 10;
    const tileSize = 50;
    let yAxis = (numRow -1 ) * tileSize;
    let xAxis = 0;
    let board = [];
    let direction = 1;

    for (let index = 0; index < numRow * numRow; index++) {
        board.push({xAxis, yAxis, tileSize, index});
        xAxis = xAxis + tileSize * direction;
        if (xAxis >= width || xAxis <= -tileSize) {
            direction*= -1;
            xAxis += tileSize * direction;
            yAxis -= tileSize
        }
    }
    console.log(board)

    return (
        <div className="display-board">{board.map(tile => {
            return <div className="tile" style={{height: `${tileSize}px`}} style={{width: `${tileSize}px`}}></div>
        })}</div>
    )
}

export default GameBoard;
