import {useEffect} from "react"

const GameBoard = () => {

    const width = 500;
    const height = 500;
    const numCol = 10;
    const numRow = 10;
    const tileSize = 50;
    let yAxis = (numRow -1 ) * tileSize;
    let xAxis = 0;
    let board = [];
    let direction = 1;

    for (let index = 0; index < numRow * numRow; index++) {
        board.push({xAxis, yAxis, tileSize, index});
        xAxis = xAxis + tileSize * direction;
        if (xAxis >= width || xAxis <= -tileSize) {
            direction*= -1;
            xAxis += tileSize * direction;
            yAxis -= tileSize
        }
    }
    console.log(board)

    const canvas = props => {
        const canvasRef = useEffect(null);
        const draw = ctx => {
            board.forEach(tile => {
                ctx.beginPath();
                ctx.rect(xAxis, yAxis, tileSize, tileSize);
                ctx.stroke();
            })
        }

        useEffect(() => {
            const canvas = canvasRef.current;
            const context = canvas.getContext('2d');
            draw(context)
        }, [draw])

    }

    return (
        <canvas ref={canvasRef} {...props}/>
    )
}

export default GameBoard;
